mapper.insert:
SELECT SEQ_PIC_FILE_IDX.NEXTVAL FROM DUAL
INSERT INTO PIC_FILE ( PIC_IDX, ORIGINAL_FILE_NAME, STORED_FILE_NAME, PIC_FILE_SIZE, MEMBER_IDX
) VALUES ( #{PIC_IDX}, #{ORIGINAL_FILE_NAME}, #{STORED_FILE_NAME}, #{FILE_SIZE}, #{MEMBER_IDX}
)

mapper.select:
		
 			SELECT MEMBER_IDX
			FROM MEMBER
			WHERE MEMBER_ID = #{MEMBER_ID}
			AND MEMBER_PASSWORD = #{MEMBER_PASSWORD}

		
 			SELECT MEMBER_ID
			FROM MEMBER
			WHERE MEMBER_ID = #{MEMBER_ID}

		
 			SELECT MEMBER_NICK
			FROM MEMBER
			WHERE MEMBER_NICK = #{MEMBER_NICK}
