mapper.update:

	update ride_record
		set
			ride_title = #{RIDE_TITLE},
			ride_content = #{RIDE_CONTENT}
	where ride_idx = #{RIDE_IDX}

mapper.delete:

	delete from ride_record
	cascade
	where ride_idx = #{RIDE_IDX}

mapper.insert:
SELECT SEQ_RIDE_PIC_FILE_IDX.NEXTVAL FROM DUAL
INSERT INTO RIDE_PIC_FILE ( RIDE_PIC_IDX, ORIGINAL_FILE_NAME, STORED_FILE_NAME, FILE_SIZE, FILE_DATE, RIDE_IDX, MEMBER_IDX
) VALUES ( #{RIDE_PIC_IDX}, #{ORIGINAL_FILE_NAME}, #{STORED_FILE_NAME}, #{FILE_SIZE}, to_char(sysdate, 'YYYY-MM-DD'), #{RIDE_IDX}, #{MEMBER_IDX} )
